sqlMap.select:
SELECT , t.tag_name, h.ctr AS count, g.group, g.group_name FROM bibtex b, tas t, groupids g, bibhash h, (SELECT t1.content_id FROM WHERE
AND t1.content_type = #contentType# AND t1.user_name = #requestedUserName# ORDER BY t1.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE tt.content_id=b.content_id AND tt.content_id=t.content_id AND AND g.group = b.group
ORDER BY b.date DESC, b.content_id DESC;

SELECT , t.tag_name, h.ctr AS count, g.group, g.group_name, FROM bibtex b, tas t, groupids g, bibhash h, (SELECT t1.content_id FROM WHERE
AND t1.content_type = #contentType# AND t1.user_name = #requestedUserName# ORDER BY t1.date DESC LIMIT #limit# OFFSET #offset#) AS tt LEFT JOIN document d USING(content_id) WHERE tt.content_id=b.content_id AND tt.content_id=t.content_id AND AND g.group = b.group
ORDER BY b.date DESC, b.content_id DESC;

SELECT , t.tag_name, h.ctr AS count, g.group, g.group_name, tt.* FROM bibtex b, tas t, groupids g, bibhash h, (SELECT t1.content_id, FROM document d, WHERE
AND t1.content_type = #contentType# AND t1.user_name = #requestedUserName# AND d.content_id = t1.content_id ORDER BY t1.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE tt.content_id=b.content_id AND tt.content_id=t.content_id AND AND g.group = b.group
ORDER BY b.date DESC, b.content_id DESC;

SELECT , t.tag_name, g.group_name, h.ctr as count, g.group FROM bibtex b, bibhash h, ((SELECT content_id, date FROM bibtex t1, groups g WHERE g.group = #groupId# AND g.user_name = t1.user_name
)UNION( SELECT content_id, date FROM bibtex b, groups g, friends f WHERE f.f_user_name = #userName# AND g.user_name = f.user_name AND b.user_name = f.user_name AND g.group = #groupId# AND b.group = $groupType$ )UNION( SELECT content_id, date FROM bibtex b, groups g WHERE b.user_name = #userName# AND g.user_name = b.user_name AND g.group = #groupId# ) ORDER BY date DESC LIMIT #limit# OFFSET #offset#) AS bb LEFT OUTER JOIN tas AS t ON bb.content_id = t.content_id, groupids AS g WHERE t.group = g.group AND AND b.content_id = bb.content_id
ORDER BY bb.date DESC, bb.content_id DESC

SELECT , t.tag_name, g.group_name, h.ctr as count, g.group, bb.* FROM bibtex b, bibhash h, ( SELECT ids.content_id, ids.date, FROM ((SELECT content_id, date FROM bibtex t1, groups g WHERE g.group = #groupId# AND g.user_name = t1.user_name
)UNION( SELECT content_id, date FROM bibtex b, groups g, friends f WHERE f.f_user_name = #userName# AND g.user_name = f.user_name AND b.user_name = f.user_name AND g.group = #groupId# AND b.group = $groupType$ )UNION( SELECT content_id, date FROM bibtex b, groups g WHERE b.user_name = #userName# AND g.user_name = b.user_name AND g.group = #groupId# )) as ids JOIN document d USING(content_id) ORDER BY date DESC LIMIT #limit# OFFSET #offset#) AS bb LEFT OUTER JOIN tas AS t ON bb.content_id = t.content_id, groupids AS g WHERE t.group = g.group AND AND b.content_id = bb.content_id
ORDER BY bb.date DESC, bb.content_id DESC

SELECT , t.tag_name, h.ctr as count,NULL AS `group`, NULL AS group_name FROM bibtex b, tas t, bibhash h, (SELECT content_id FROM search_bibtex s WHERE MATCH (s.author) AGAINST (#search# IN BOOLEAN MODE) AND s.group = #groupId#
ORDER BY s.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE b.content_id = tt.content_id AND t.content_id = tt.content_id
AND ORDER BY b.date DESC, b.content_id DESC;

SELECT , t.tag_name, h.ctr as count,NULL AS `group`, NULL AS group_name FROM bibtex b, tas t, bibhash h, (SELECT s.content_id FROM search_bibtex s, WHERE MATCH(s.author) AGAINST (#search# IN BOOLEAN MODE) AND s.group = #groupId# AND s.content_id = t1.content_id
AND ORDER BY s.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE b.content_id = tt.content_id AND t.content_id = tt.content_id
AND ORDER BY b.date DESC, b.content_id DESC;

SELECT , b.interHash, b.intraHash, t.tag_name, b.ctr AS count, NULL AS `group`, NULL AS group_name FROM ( SELECT , h.ctr,
MAX(IF (LCASE(upper) = LCASE(#tagIndex[].tagName#), 1, 0)) AS s$tagIndex[].index$
FROM (
SELECT LCASE(upper) AS upper, LCASE(lower) AS lower FROM tagtagrelations t WHERE LCASE(upper) = LCASE(#tagIndex[].tagName#) AND t.user_name = #requestedGroupName# UNION SELECT LCASE(#tagIndex[].tagName#), LCASE(#tagIndex[].tagName#)
) AS tags JOIN tas t1 ON ( t1.tag_lower = tags.lower AND t1.content_type = 2
) JOIN bibtex b2 USING(content_id) JOIN bibhash h ON (h.hash = b2.simhash1 AND h.type = 1) JOIN () AS groupmembers ON (groupmembers.user_name = t1.user_name) GROUP BY t1.content_id HAVING
s$tagIndex[].index$ > 0
ORDER BY t1.date DESC LIMIT #limit# OFFSET #offset# ) AS b JOIN tas t ON b.content_id = t.content_id
WHERE b.entrytype = #systemTags.EntryType.entryType#
ORDER BY b.date DESC;
