sqlMap.select:
SELECT , t.tag_name, h.ctr as count,NULL AS `group`, NULL AS group_name FROM bibtex b,tas t,bibhash h, (SELECT content_id FROM search s WHERE MATCH (s.author) AGAINST (#search# IN BOOLEAN MODE) AND s.content_type= #contentType# AND s.group = #groupType#
ORDER BY s.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE b.content_id = tt.content_id AND t.content_id=tt.content_id
AND ORDER BY b.date DESC,b.content_id DESC;

SELECT , t.tag_name, h.ctr as count,NULL AS `group`, NULL AS group_name FROM bibtex b,tas t,bibhash h, (SELECT s.content_id FROM search s, tas t1 WHERE MATCH(s.author) AGAINST (#search# IN BOOLEAN MODE) AND s.content_type =#contentType# AND s.group =#groupType# AND s.content_id = t1.content_id
AND t1.tag_name = ORDER BY s.date DESC LIMIT #limit# OFFSET #offset#) AS tt WHERE b.content_id = tt.content_id AND t.content_id=tt.content_id
AND ORDER BY b.date DESC,b.content_id DESC;

SELECT , b.interHash, b.intraHash, t.tag_name, b.ctr AS count, NULL AS `group`, NULL AS group_name FROM ( SELECT , h.ctr,
MAX(IF (LCASE(upper) = LCASE(#tagIndex[].tagName#), 1, 0)) AS s$tagIndex[].index$
FROM (
SELECT LCASE(upper) AS upper, LCASE(lower) AS lower FROM tagtagrelations t WHERE LCASE(upper) = LCASE(#tagIndex[].tagName#) AND t.user_name = #requestedGroupName# UNION SELECT LCASE(#tagIndex[].tagName#), LCASE(#tagIndex[].tagName#)
) AS tags JOIN tas t1 ON ( t1.tag_lower = tags.lower AND t1.content_type = 2
) JOIN bibtex b2 USING(content_id) JOIN bibhash h ON (h.hash = b2.simhash1 AND h.type = 1) JOIN () AS groupmembers ON (groupmembers.user_name = t1.user_name) GROUP BY t1.content_id HAVING
s$tagIndex[].index$ > 0
ORDER BY t1.date DESC LIMIT #limit# OFFSET #offset# ) AS b JOIN tas t ON b.content_id = t.content_id ORDER BY b.date DESC;
