sqlMap.select:
SELECT , t.change_date, t.tag_name, g.*, b2.count, , FROM (SELECT t1.*, pp.person_change_id, pp.qualifying, pp.person_id, pp.relator_code, pp.log_changed_by, pp.person_index, h.ctr AS count FROM bibtex t1, bibhash h, pub_person pp WHERE
AND t1.simhash1=pp.simhash1 AND pp.person_id=#personRelation.person.personId#
) AS b2 LEFT OUTER JOIN tas AS t ON b2.content_id = t.content_id , groupids AS g WHERE b2.group = g.group ORDER BY b2.date DESC, b2.content_id DESC
