INSERT INTO log_inboxMail (message_id, content_id, intraHash, sender_user, receiver_user, date, content_type, log_date) SELECT message_id, content_id, intraHash, sender_user, receiver_user, date, content_type, NOW() as log_date FROM inboxMail WHERE
inboxMail.message_id = #messageId#
inboxMail.receiver_user = #receiver#;
